mapper.select:
select * from ms_order where 1=1 

	and status = #{status}


	and flag = #{flag}


	and opend_id = #{opendId}


	and sys_id = #{sysId}


	and is_del = #{isDel}


	and is_pl = #{isPl}


	and (is_use = #{isUse}
	or is_shouhuo = #{isShouhuo})


	and (is_send = #{isSend} or is_send = '2')


	and order_no like "%"#{orderNo}"%"


	and to_days(create_time) <= to_days(now())
 
 order by create_time desc

mapper.update:
update ms_order set is_use = #{isUse}

	,is_send = #{isSend}

where sys_id = #{sysId}

update ms_order set is_shouhuo = '1'

	,is_send = #{isSend}

where sys_id = #{sysId}

update ms_order set update_time = now()

	,status = #{status}


	,pay_type = #{payType}


	,pay_money = #{payMoney}


	,free_money = #{freeMoney}


	,discount = #{discount}

 where sys_id = #{sysId}
